+        Thread t2 = new Thread(new Runnable()
+        {
+            public void run()
+            {
+                Map<ApplicationState, VersionedValue> states = new EnumMap<>(ApplicationState.class);
+                states.put(ApplicationState.INTERNAL_IP, valueFactory.internalIP(ip));
