+        while (tg.getParent() != null)
+        {
+            tg = tg.getParent();
+        }
+        
+        // estimate of groups        
+        ThreadGroup[] allGroups = new ThreadGroup[tg.activeGroupCount()];
+        int actual;
+        for (;;)
