+  /**
+   * Returns mainId and childId formatted like "-01-01"
+   */
+  private String formatIds(final int mainId, final int childId) {
+    return "-" + formatId(mainId) + "-" + formatId(childId);
+  }
+
+  /**
+   * Returns id formatted like "01"
