+package org.apache.dolphinscheduler.alert.utils;
+
+import org.apache.commons.io.IOUtils;
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+import java.io.IOException;
+import java.io.InputStream;
+import java.util.Properties;
+
+import static org.apache.dolphinscheduler.alert.utils.Constants.ALERT_PROPERTIES_PATH;
+import static org.apache.dolphinscheduler.alert.utils.Constants.DATA_SOURCE_PROPERTIES_PATH;
+
+/**
+ * property utils
+ * single instance
+ */
+public class PropertyUtils {
+
+    /**
+     * logger
+     */
+    private static final Logger logger = LoggerFactory.getLogger(PropertyUtils.class);
+
+    private static final Properties properties = new Properties();
+
+    private static final PropertyUtils propertyUtils = new PropertyUtils();
+
+    private PropertyUtils(){
+        init();
+    }
+
+    private void init(){
+        String[] propertyFiles = new String[]{ALERT_PROPERTIES_PATH};
+        for (String fileName : propertyFiles) {
+            InputStream fis = null;
+            try {
+                fis = PropertyUtils.class.getResourceAsStream(fileName);
+                properties.load(fis);
+
+            } catch (IOException e) {
+                logger.error(e.getMessage(), e);
+                if (fis != null) {
+                    IOUtils.closeQuietly(fis);
+                }
+                System.exit(1);
+            } finally {
+                IOUtils.closeQuietly(fis);
+            }
+        }
+    }
+
+    /**
+     * get property value
+     * @param key property name
+     * @return the value
+     */
+    public static String getString(String key) {
+        return properties.getProperty(key);
+    }
+
+    /**
+     * get property value
+     *
+     * @param key property name
+     * @return  get property int value , if key == null, then return -1
+     */
+    public static int getInt(String key) {
+        return getInt(key, -1);
+    }
+
+    /**
+     * get int value
+     * @param key the key
+     * @param defaultValue the default value
+     * @return the value related the key or the default value if the key not existed
+     */
+    public static int getInt(String key, int defaultValue) {
+        String value = getString(key);
+        if (value == null) {
+            return defaultValue;
+        }
+
+        try {
+            return Integer.parseInt(value);
+        } catch (NumberFormatException e) {
+            logger.info(e.getMessage(),e);
+        }
+        return defaultValue;
+    }
+
+    /**
+     * get property value
+     * @param key property name
+     * @return  the boolean result value
+     */
+    public static Boolean getBoolean(String key) {
+        String value = properties.getProperty(key.trim());
+        if(null != value){
+            return Boolean.parseBoolean(value);
+        }
+
+        return false;
+    }
+
+    /**
+     * get long value
+     * @param key the key
+     * @return if the value not existed, return -1, or will return the related value
+     */
+    public static long getLong(String key) {
+        return getLong(key,-1);
+    }
+
+    /**
+     * get long value
+     * @param key the key
+     * @param defaultVal the default value
+     * @return the value related the key or the default value if the key not existed
+     */
+    public static long getLong(String key, long defaultVal) {
+        String val = getString(key);
+        return val == null ? defaultVal : Long.parseLong(val);
+    }
+
+
+    /**
+     * get double value
+     * @param key the key
+     * @param defaultVal the default value
+     * @return the value related the key or the default value if the key not existed
+     */
+    public double getDouble(String key, double defaultVal) {
+        String val = getString(key);
+        return val == null ? defaultVal : Double.parseDouble(val);
+    }
+
+
+    /**
+     *  get array
+     * @param key       property name
+     * @param splitStr  separator
+     * @return the result array
+     */
+    public static String[] getArray(String key, String splitStr) {
+        String value = getString(key);
+        if (value == null) {
+            return null;
+        }
+        try {
+            String[] propertyArray = value.split(splitStr);
+            return propertyArray;
+        } catch (NumberFormatException e) {
+            logger.info(e.getMessage(),e);
+        }
+        return null;
+    }
+
+    /**
+     * get enum
+     * @param key the key
+     * @param type the class type
+     * @param defaultValue the default value
+     * @param <T> the generic class type
+     * @return  get enum value
+     */
+    public <T extends Enum<T>> T getEnum(String key, Class<T> type,
+                                         T defaultValue) {
+        String val = getString(key);
+        return val == null ? defaultValue : Enum.valueOf(type, val);
+    }
+}

