+            args.add(instanceFile);
+            args.add("--py");
+            args.add(originalCodeFileName);
+            args.add("--logging_directory");
+            args.add(logDirectory);
+            args.add("--logging_file");
+            args.add(instanceConfig.getFunctionDetails().getName());
