+
+        boolean includeZero() default false;
+    }
+
+
+    /**
+     * Checks if resources specified are valid
+     */
+    @Retention(RetentionPolicy.RUNTIME)
+    @Target(ElementType.FIELD)
+    public @interface isValidResources {
