+        when(ref.getMessage()).thenReturn(message);
+        when(message.newReference()).thenReturn(ref);
+        when(message.getMessageNumber()).thenReturn(messageNumber);
+        for(BaseQueue q : queues)
+        {
+            q.enqueue(message, null);
+        }
+
+        return queues.size();
+    }
