+        // Table of users
+        // Username(string), Access rights Read,Write,Admin(bool,bool,bool)
+
+        reloadData();
+        
+        if (_userlistDataType == null)
+        {
+            _logger.warn("TabluarData not setup correctly");
+            return null;
