+        final int result = stateless.execute(param);
+        assertEquals(result, param);
+    }
+
+    @Module
+    @Classes(cdi = true)
+    public StatelessBean bean() {
+        final StatelessBean bean = new StatelessBean(StatelessTestBean.class);
+        bean.setLocalBean(new Empty());
