+                + "insert into t2 values 'TR01'");
+        s.execute("create trigger tr02 after insert on t1 "
+                + "when (current_user <> 'U2') "
+                + "insert into t2 values 'TR02'");
+        s.execute("grant insert on t1 to u2");
+
