+      try {
+        filePos = reader.getPosition();
+        if (start == filePos) {
+          progress = 0.0f;
+        } else {
+          long readBytes = filePos - start;
+          long remainingBytes = Math.max(end - filePos, 0);
+          progress = Math.min(1.0f, (float) (readBytes) / (float) (readBytes + remainingBytes));
+        }
+      } catch (IOException e) {
+        progress = 0.0f;
+      }
+      return progress;
+    }
+  }
 }

